var str = "";
str = str.split("");

var substr = "ABCDA";

var shortestSubstr = new Array(str.length);

for (var startIndex = 0; startIndex < str.length-substr.length; startIndex++) {
	if (substr.indexOf(str[startIndex]) < 0) {
		continue;
	}
	var substrclone = substr.split("");
	var endIndex = startIndex;
	while (substrclone.length > 0 && endIndex < str.length) {
		var letterIndex = substrclone.indexOf(str[endIndex]);
		if (letterIndex >= 0) {
			substrclone.splice(letterIndex, 1);
		}
		endIndex++;
	}
	if (substrclone.length == 0 && endIndex - startIndex < shortestSubstr.length) {
		shortestSubstr = str.slice(startIndex, endIndex);
	}
}

console.log(shortestSubstr.join(""));
