"use strict";

// Start a timer
console.time("Solved");

// The string to search in
const str = "".split("");

// The substring to find the letters of
const substr = "ABCDA";

// The currently shortest substring with all characters
let shortestSubstr = new Array(str.length);

// Search the string
for (let startIndex = 0; startIndex < str.length-substr.length; startIndex++) {
	if (substr.indexOf(str[startIndex]) < 0) {
		continue;
	}
	let substrclone = substr.split("");
	let endIndex = startIndex;
	while (substrclone.length > 0 && endIndex < str.length) {
		let letterIndex = substrclone.indexOf(str[endIndex]);
		if (letterIndex >= 0) {
			substrclone.splice(letterIndex, 1);
		}
		endIndex++;
	}
	if (substrclone.length == 0 && endIndex - startIndex < shortestSubstr.length) {
		shortestSubstr = str.slice(startIndex, endIndex);
	}
}

// Solved!
console.timeEnd("Solved");
console.log(shortestSubstr.join(""));
